class App extends Component {
    constructor() {
        super()
        this.state = {
            manager: '',
            winner: '',
        }
    }

    // 内置钩子函数，在页面渲染之后调用
    componentDidMount() {

    }

    // 内置钩子函数，在页面渲染之前调用
    async componentWillMount() {
        let _manager = await lotteryInstance.methods.manager().call()
        this.setState({
            manager: _manager,
        })
    }

    // 卸载钩子函数

    render() {

      return (
          <div>
              <p> Hello, World </p>
              <p> manager: {this.state.manager} </p>
          </div>
      );
    }
}

export default App;
