import React, {Component} from 'react';

import web3 from "./utils/initWeb3"
import lotteryInstance from "./eth/lotteryInstance"

//function App() {
//  return (
//      <p> Hello, World </p>
//  );
//}

console.log(lotteryInstance.methods.manager().call())

class App extends Component {
    constructor() {
        super()
        this.state = {
            manager: '',
            round: 0,
            winner: '',
            playersCount: 0,
            balance: 0,
            players: [],
        }
    }

    // 内置钩子函数，在页面渲染之后调用
    componentDidMount() {

    }

    // 内置钩子函数，在页面渲染之前调用
    async componentWillMount() {
        let _manager = await lotteryInstance.methods.manager().call()
        let _round = await lotteryInstance.methods.round().call()
        let _winner = await lotteryInstance.methods.winner().call()
        let _playersCount = await lotteryInstance.methods.getPlayersCount().call()
        let _balance = await lotteryInstance.methods.getBalance().call()
        let _players = await lotteryInstance.methods.getPlayers().call()
        this.setState({
            manager: _manager,
            round: _round,
            winner: _winner,
            playersCount: _playersCount,
            balance: _balance,
            players: _players,
        })
    }

    // 卸载钩子函数

    render() {

      return (
          <div>
              <p> Hello, World </p>
              <p> manager: {this.state.manager} </p>
              <p> round: {this.state.round} </p>
              <p> winner: {this.state.winner} </p>
              <p> playersCount: {this.state.playersCount} </p>
              <p> balance: {this.state.balance} </p>
              <p> players: {this.state.players} </p>
          </div>
      );
    }
}

export default App;
